import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Tabs, Tab, Form, Button, Alert } from 'react-bootstrap';
import axios from 'axios';
import PatientForm from './PatientForm';
import { getPatient, authorizeDoctor } from '../utils/blockchain';

// Hardcode the API URL since environment variables aren't working
const API_URL = "http://localhost:5000";

const DoctorDashboard = ({ contract, address }) => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [searchId, setSearchId] = useState('');
  const [patientData, setPatientData] = useState(null);
  const [authForm, setAuthForm] = useState({
    patientId: '',
    doctorAddress: ''
  });
  const [authSuccess, setAuthSuccess] = useState('');

  const handleSearch = async () => {
    if (!searchId) return;
    
    setLoading(true);
    setError('');
    setPatientData(null);
    
    try {
      // Get patient data from blockchain
      const blockchainData = await getPatient(contract, searchId);
      
      // Get additional metadata from backend
      console.log(`Fetching patient metadata from: ${API_URL}/api/patients/${searchId}`);
      const response = await axios.get(`${API_URL}/api/patients/${searchId}`);
      console.log("Patient metadata received:", response.data);
      
      setPatientData({
        ...blockchainData,
        ...response.data.metadata
      });
    } catch (err) {
      console.error('Error fetching patient:', err);
      setError('Failed to fetch patient data. Make sure the patient exists and you have access.');
    } finally {
      setLoading(false);
    }
  };

  const handleAuthChange = (e) => {
    setAuthForm({
      ...authForm,
      [e.target.name]: e.target.value
    });
  };

  const handleAuthorizeDoctor = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setAuthSuccess('');
    
    try {
      // Authorize doctor on blockchain
      await authorizeDoctor(contract, authForm.patientId, authForm.doctorAddress);
      
      setAuthSuccess(`Doctor ${authForm.doctorAddress.substring(0, 8)}... has been authorized to access patient #${authForm.patientId}`);
      
      // Reset form
      setAuthForm({
        patientId: '',
        doctorAddress: ''
      });
    } catch (err) {
      console.error('Error authorizing doctor:', err);
      setError('Failed to authorize doctor. Make sure you have permission to authorize doctors for this patient.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="py-5">
      <h2 className="mb-4">Doctor Dashboard</h2>
      
      <Tabs defaultActiveKey="search" className="mb-4">
        <Tab eventKey="search" title="Search Patient">
          <Card>
            <Card.Body>
              <h3>Search Patient Records</h3>
              
              <Form className="d-flex mb-4">
                <Form.Control
                  type="number"
                  placeholder="Enter Patient ID"
                  value={searchId}
                  onChange={(e) => setSearchId(e.target.value)}
                  className="me-2"
                />
                <Button 
                  variant="primary" 
                  onClick={handleSearch}
                  disabled={loading || !searchId}
                >
                  Search
                </Button>
              </Form>
              
              {error && <Alert variant="danger">{error}</Alert>}
              
              {patientData && (
                <div className="mt-4">
                  <h4>Patient Information</h4>
                  <Row>
                    <Col md={6}>
                      <p><strong>Name:</strong> {patientData.name}</p>
                      <p><strong>Age:</strong> {patientData.age}</p>
                    </Col>
                    <Col md={6}>
                      <p><strong>Disease:</strong> {patientData.disease}</p>
                      <p><strong>Doctor Assigned:</strong> {patientData.doctorAssigned.substring(0, 8)}...</p>
                    </Col>
                  </Row>
                  
                  <h4 className="mt-4">Additional Notes</h4>
                  <p>{patientData.doctorNotes || 'No additional notes available.'}</p>
                </div>
              )}
            </Card.Body>
          </Card>
        </Tab>
        
        <Tab eventKey="add" title="Add Patient">
          <Card>
            <Card.Body>
              <PatientForm contract={contract} mode="add" />
            </Card.Body>
          </Card>
        </Tab>
        
        <Tab eventKey="update" title="Update Patient">
          <Card>
            <Card.Body>
              <PatientForm contract={contract} mode="update" />
            </Card.Body>
          </Card>
        </Tab>
        
        <Tab eventKey="authorize" title="Authorize Doctor">
          <Card>
            <Card.Body>
              <h3>Authorize Another Doctor</h3>
              
              {error && <Alert variant="danger">{error}</Alert>}
              {authSuccess && <Alert variant="success">{authSuccess}</Alert>}
              
              <Form onSubmit={handleAuthorizeDoctor}>
                <Form.Group className="mb-3">
                  <Form.Label>Patient ID</Form.Label>
                  <Form.Control
                    type="number"
                    name="patientId"
                    value={authForm.patientId}
                    onChange={handleAuthChange}
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Doctor's Ethereum Address</Form.Label>
                  <Form.Control
                    type="text"
                    name="doctorAddress"
                    value={authForm.doctorAddress}
                    onChange={handleAuthChange}
                    required
                    placeholder="0x..."
                  />
                </Form.Group>
                
                <Button variant="primary" type="submit" disabled={loading}>
                  {loading ? 'Processing...' : 'Authorize Doctor'}
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Tab>
      </Tabs>
    </Container>
  );
};

export default DoctorDashboard;
